     ```typescript
     describe('diceTool', () => {
       it('should persist messages in message history when sending to Telegram', async () => {
         // Setup mocks for messageHistoryService and telegramService
         const messageHistoryService = { createToolCallMessage: jest.fn().mockResolvedValue({ id: 'test-id' }) };
         const telegramService = { sendMessage: jest.fn() };

         const tool = new DiceTool({ messageHistoryService, telegramService });
